import { Post, BodyParam, UnauthorizedException, Inject, SecurityService } from '@nihasoft/the-way';
import { Observable, of } from 'rxjs';

export class UserRest {
    @Inject() securityService: SecurityService;

    user: {
        username: 'anakin',
        password: 'I\'m the force',
        profiles: ['master', 'jedi']
    }
    @Post('/sign/in')
    public signIn(@BodyParam signInForm: any): Observable<string> {
        if (this.user.username === signInForm.username && this.user.password === signInForm.password) {
            return of(this.securityService.generateToken({
                username: this.user.username,
                profiles: this.user.profiles
            }));
        } else {
            throw new UnauthorizedException('Username or password is incorrect.');
        }
    }
}

import { TheWayApplication, Application, Inject } from '@nihasoft/the-way';
import { HeroRest} from './hero.rest';
import { UserRest } from './user.rest';

@Application()
export class Main extends TheWayApplication {

    @Inject() heroRest: HeroRest;
    @Inject() userRest: UserRest;

    public start(): void {
        console.log('I\'m ready.');
    }
}

heroes: Array<HeroModel> = [{
    id: 10,
    name: 'Batman',
    power: 10000
}];
@Get('/heroes')
public getHeroes(): Observable<Array<HeroModel>> {
    return of(this.heroes);
}

@Get('/hero/{id}')
public getHero(@PathParam('id') id: number): Observable<HeroModel> {
    const hero = this.getHeroById(id);
    if (!hero) {
        throw new NotFoundException('Hero not found');
    }
    return of(hero);
}
public getHeroById(id: number): HeroModel {
    return this.heroes.find((hero:HeroModel) => hero.id === id)
}

@Get('/hero')
public getHeroesWithQuery(@QueryParam query: {ids: Array<number>}): Observable<Array<HeroModel>> {
    const heroes: Array<HeroModel> = [];
    for (const id of query.ids) {
        const hero: HeroModel = this.getHeroById(id);
        if (hero) {
            heroes.push(hero);
        }
    }
    return of(heroes);
}

@Post('/hero')
public createHero(@BodyParam newHero: HeroModel): Observable<HeroModel> {
    if (this.hasHero(newHero)) {
        throw new BadRequestException('The hero is registered.');
    }
    this.heroes.push(newHero);
    return of(newHero);
}
public hasHero(newHero: HeroModel): boolean {
    return this.getHeroById(newHero.id) !== null;
}

@Delete('/hero/{id}', true, ['master'])
public deleteHero(@PathParam('id') id: number): Observable<boolean> {
    let hero: HeroModel;
    for (let i = 0; i < this.heroes.length; i++) {
        if (this.heroes[i].id === id) {
            hero = this.heroes.splice(i, 1)[0];
            break;
        }
    }
    if (!hero) {
        throw new BadRequestException('The hero not exists.');
    }
    return of(true);
}

@Put('/hero', true)
public updateHero(@BodyParam updateHero: HeroModel): Observable<HeroModel> {
    const hero = this.getHeroById(updateHero.id);
    if (!hero) {
        throw new BadRequestException('The hero not exists.');
    }
    hero.name = updateHero.name;
    hero.power = updateHero.power;
    return of(hero);
}

